package Week1Assignments;

public class FindEvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int maxRange=20;
		System.out.print("List of odd numbers from 1 to 10       :");
		for(int i=1;i<maxRange;i++) {
			if(i%2==0)
			System.out.print(i+",");
	}

}
}